def split_file(filename, size_in_mb):
    chunk_size = size_in_mb * 1024 * 1024  # 转换为字节
    part_number = 1
    part_files = []
    with open(filename, 'rb') as f:
        chunk = f.read(chunk_size)
        while chunk:
            with open(f"{filename}_part_{part_number}", 'wb') as chunk_file:
                chunk_file.write(chunk)
                part_files.append(f"{filename}_part_{part_number}")
            part_number += 1
            chunk = f.read(chunk_size)
    print(part_files)

